    float progress = (float) i / nbLoop;
    fireProgressing("Progress : ", progress*100f, false);
}

for(int i = 0; i<nbLoop; i++)  {

    // regarde l'état du process à chaque itération.
    if(isCanceled()){
            throw new CancellationException("Processus canceled");
    }

    // traitement
}
